"use strict";
var _data = {};
var _session = {
	setData(name, value) {
		var old = _data[name];
		if (old == value) return;

		if (old && old.onDestroy) {
			old.onDestroy();
		}
		if (typeof value == "undefined") delete _data[name];
		else _data[name] = value;
	},
	getData(name) {
		return _data[name];
	},
	_reset() {
		_data = {};
	}
};

export const sessionManager = {
	sessionByCookie(cookie) {
		return _session;
	},
	setup() {},
	setHost(value) {
		_session.host = value;
	}
};