// Type definitions for jest-enzyme 2.1.2
// Project: https://github.com/blainekasten/enzyme-matchers/tree/master/packages/jest-enzyme
// Help: https://github.com/blainekasten/enzyme-matchers
declare namespace jest {
    interface Matchers {
        toBeChecked(): void;
        toBeDisabled(): void;
        toBeEmpty(): void;
        toBePresent(): void;
        toContainReact(reactInstance: any): void;
        toHaveClassName(className: string): void;
        toHaveHTML(html: string): void;
        toHaveProp(propName: string): void;
        toHaveRef(refName: string): void;
        toHaveState(stateName: string, state: any): void;
        toHaveStyle(styleName: string, style: any): void;
        toHaveTagName(tagName: string): void;
        toHaveText(text: string): void;
        toIncludeText(text: string): void;
        toHaveValue(value: any): void;
        toMatchElement(reactInstance: any): void;
        toMatchSelector(selector: string): void;
    }
}