"use strict";
import * as React from 'react';
import { Icon } from '../../common/icon/icon';
import { Severity } from '../alert';

const iconName: any = {
	error: "quick_error",
	warning: "quick_alert",
	info: "quick_info",
	success: "quick_info"
};

export function getSeverityCss(severity: Severity): string {
	return " s_" + severity;
}

export function getIconName(severity: Severity): string {
	return iconName[severity];
}

export class SeverityIcon extends React.Component<{
	severity: Severity,
	className: string

}, {}> {
	render() {
		return <Icon name={iconName[this.props.severity]}
			className={this.props.className + " s_" + this.props.severity} />
	}
}