"use strict";
import * as React from 'react';
import * as Sdata from '../../dataStore/sdata';
import { Link } from '../../link/link';

export class PanelAlertLinks extends React.Component<{
	links: any,
	onClick: (link: Sdata.ILink) => void
}, {}>{
	render() {
		let links: any[] = [];
		for (let bind in this.props.links) {
			let link = this.props.links[bind];
			links.push(
				<Link className="s_alertpanel_link"
					title={link.$title}
					value={link}
					key={links.length}
					onClick={this.props.onClick} />);
		}
		return <div className="s_alertpanel_links">{links}</div>;
	}
}