"use strict";
import * as React from 'react';
import Scrollbar from '../../../common/scroll/scrollbar/scrollbar';

export default class List extends React.Component<{
    className?: string;
    maxWidth?: number;
    width?: number;
    maxHeight?: number;
    height?:number;
}, {}> {
    private css_ul: string;
    private css: string;
    private css_li: string;
    constructor(props: any, context: any) {
        super(props, context);
        this.css = props.className || "s_list_primary";
        this.css_ul = "s_list_ul " + this.css + "_ul";
        this.css_li = "s_list_li " + this.css + "_li";
    }
    //  {this.props.children}
    render() {
        return <Scrollbar>
            <ul className={this.css_ul}>
                {React.Children.map(this.props.children, (child: React.ReactChild) => {
                    return <li className={this.css_li} >{child}</li>;
                })}
            </ul></Scrollbar>
    }
    
}
