"use strict";
import * as React from 'react';
import { Icon } from '../../../common/icon/icon';
import { IFieldProps, IDetailFieldProps, EditField, DetailField } from '../field';

export class PhoneDetail extends React.Component<IFieldProps, {}> {
    render() {
        let value: any;
        if (this.props.value != null && this.props.value.length) {
            value = <a className={"s_link_default"} href={"tel:" + this.props.value}>
                <Icon name="phone" />
                {" " + this.props.value}
            </a>;
        }
        return <DetailField {...this.props as IDetailFieldProps}>{value}</DetailField>;
    }
}

export class PhoneField extends React.Component<IFieldProps, {}> {
    render() {
        if (this.props.hidden) {
            return <noscript />;
        }
        if (this.props.editable) {
            return <EditField inputType="tel" titleIcon="phone"{...this.props} />
        }
        return <PhoneDetail {...this.props} />;
    }
}