"use strict";
import * as React from 'react';
import * as Sdata from '../../../dataStore/sdata';
import { IFieldProps, IDetailFieldProps, EditField, DetailField } from '../field';

export class StringField extends React.Component<IFieldProps, {}> {
    static get sdataType() {
        return Sdata.type.string;
    }
    render() {
        if (this.props.hidden) {
            return <noscript />;
        }
        if (this.props.editable) {
            return <EditField inputType="text" {...this.props} />;
        }
        return <DetailField {...this.props as IDetailFieldProps}>{this.props.value}</DetailField>;
    }
}