"use strict";
import { EmailField } from './emailField';
import { PhoneField } from './phoneField';
import { UrlField } from './urlField';
import { StringField } from './stringField';
import { IFieldProps} from '../field';

let format: any = {
    $email: EmailField,
    $phone: PhoneField,
    $url: UrlField
}
export function getSdataType() {
    return StringField.sdataType;
}
export function getFieldClass(props: IFieldProps): StringField {
    return format[props.meta.$format] || StringField;
}