"use strict";
import * as React from 'react';
import { Icon } from '../../../common/icon/icon';
import { IFieldProps, IDetailFieldProps, EditField, DetailField } from '../field';

export class UrlDetail extends React.Component<IFieldProps, {}> {
    render() {
        let value: any;
        if (this.props.value != null && this.props.value.length) {
            value = <a className={"s_link_default"}
                href={this.props.value}
                target="blank">
                <Icon name="hyperlink" />
                {" " + this.props.value + " "}
                <Icon name="export" className="s_open_in_new" />
            </a>;
        }
        return <DetailField {...this.props as IDetailFieldProps}>{value}</DetailField>;
    }
}

export class UrlField extends React.Component<IFieldProps, {}> {
    render() {
        if (this.props.hidden) {
            return <noscript />;
        }
        if (this.props.editable) {
            return <EditField inputType="url" titleIcon="hyperlink"  {...this.props} />;
        }
        return <UrlDetail {...this.props} />;
    }
}
