"use strict";
import * as React from 'react';
import { Tip, TipTitle, TipDescription } from '../over/tip/tip';

interface IProps {
    of: React.ReactInstance,
    title: string,
    description?: string,
    shortcuts?: string[]
}
export class LinkTip extends React.Component<IProps, {}> {
    render() {
        let shorcuts:string;
        if(this.props.shortcuts){
            shorcuts = this.props.shortcuts.join(",").replace("+"," ").toUpperCase();
        }
        return <Tip of={this.props.of}>
            <TipTitle text={this.props.title} />
            <TipTitle text={this.props.description} />
            <TipDescription text={shorcuts} />
        </Tip>;
    }
}