"use strict";
import * as React from 'react';
import * as ReactDom from 'react-dom';
import { getLocalString } from '../../../culture/localString';
import { Icon } from '../../../common/icon/icon';
import { Link, LinkClickHandler } from '../../../link/link';
import { SitemapMenu, setMenuDescription } from '../../sitemap/items';

export class Reorder extends React.Component<{
    onStart: (handle: HTMLElement) => void
}, {}>{
    handleMouseDown = () => {
        this.props.onStart(ReactDom.findDOMNode(this) as HTMLElement);
    }
    render() {
        return <div className="s_sitemapedit_sortable_handler" onMouseDown={this.handleMouseDown}>
            <Icon name="more_vertical" />
        </div>;
    }
}