"use strict";
const OFFICE_ROOT = "msoffice/lib/";

export interface IOfficeController {
    product: string
}
export class OfficeController implements IOfficeController {
    static detect(): OfficeController {
        if (document.location.href.indexOf("msoffice/lib") >= 0) {
            return new OfficeController(document.location.href);
        }
    }
    readonly product: string;
    constructor(url: string) {
        if (url.indexOf( OFFICE_ROOT + "excel") >= 0) {
            this.product = "excel";
        }
        else {
            if (url.indexOf(OFFICE_ROOT + "word") >= 0) {
                this.product = "word";
            }
            else {
                this.product = "ppt";
            }
        }
    }   
}