"use strict";
import * as React from 'react';
import { TrackersController } from '../tracker/component/trackersController';
import { Logo } from '../profile/bar/logo';
import { Profile } from '../profile/profile';
import { getLocalString } from '../culture/localString';
import { syra_site } from '../syra/utility/syra_site';

export class OfficeProfileBar extends React.Component<{
	profile: Profile
}, {}>{
	render() {
		return <header className="s_profile_bar">
			<ul className="s_profile_bar_bar">
				<li><Logo productName={this.props.profile.productName} /></li>
				<li className="s_profile_bar_space_fill" />
				<li><TrackersController /></li>
				{syra_site._hasNewAddin && <li><a href="/msoffice/lib/general/addIn/SyracuseOfficeAddinsSetup.EXE"
					className="s_profile_bar_textlink">{getLocalString("excel_new_addin")}</a></li>}
				<li><div className="s_profile_bar_textlink">{this.props.profile.fullName}</div></li>
			</ul>
		</header>;
	}		
}