"use strict";
import * as React from 'react';
import { getLocalString } from '../../culture/localString';
import { Link } from '../../link/link';
import { route } from '../../route/route';
import './history.less';

export class HistoryLinks extends React.Component<{
	onGoto: (goBack: boolean) => void
}, {}>{
	render() {
		return <div className="s_page_history">
			<Link
				icon="prev"
				iconText="left"
				className="s_page_history_back"
				title={getLocalString("route_history_prev")}
				disabled={!route.isX3Previous}
				value={true}
				onClick={this.props.onGoto} />
			<Link
				icon="next"
				iconText="right"
				className="s_page_history_forward"
				title={getLocalString("route_history_next")}
				disabled={!route.isX3Next}
				value={false}
				onClick={this.props.onGoto} />
		</div>;
	}
}