"use strict";
import * as React from 'react';
import * as ReactDom from 'react-dom';
import * as Dom from '../utility/html/dom';
import * as Events from '../event/events';


export class SyraMainPage extends React.Component<{
    onDidMount: (body: HTMLElement) => void,
    onResize?: () => void
}, {}>{
    private slot: HTMLElement;
    componentDidMount() {
        this.props.onDidMount(this.slot);
        Events.resize.subscribe(this.props.onResize);
    }
    componentWillUnmount() {
        Events.resize.unsubscribe(this.props.onResize);
        Dom.empty(this.slot);
        this.slot = null;
    }
    render() {
        return <div className="s_syra_page" ref={(node) => this.slot = node}></div>;
    }
}