"use strict";
import * as React from 'react';
import { Link } from '../../link/link';
import { getLocalString } from '../../culture/localString';
import { PopupLink } from '../../over/popup/popupLink'
import { IHelpProps } from './profileBarProps';
import { HotKeyLinks } from '../../shortcut/hotKeyLinks';

export class HelpLink extends React.Component<IHelpProps, {}>{
	onRenderPopup = () => {
		return <HotKeyLinks>
			<Link title={getLocalString("profile_help_center")} className="s_profile_bar_list_item" onClick={this.props.onGotoHelpCenter} />
			<Link title={getLocalString("profile_help_shortcuts")} className="s_profile_bar_list_item" onClick={this.props.onGotoHelpShortcuts} />
		</HotKeyLinks>;
	}
	render(){
		return <PopupLink
			icon="help"
			className="s_profile_bar_iconlink"
			title={getLocalString("profile_help")}
			popup={{
				render: this.onRenderPopup
			}}
		/>
	}
}