"use strict";
import * as React from 'react';
import { Link } from '../../link/link';
import { keyCommands } from '../../shortcut/keySpot';
import './logo.less';

export class LogoLink extends React.Component<{
	productName: string,
	title: string,
	onGoHome: () => void
}, {}> {
	render() {
		let css = "s_profile_bar_logo";
		if (this.props.productName) {
			css += " s_profile_bar_logo_" + this.props.productName.trim().toLowerCase();
		}
		return <Link
			title={this.props.title}
			icon="css"
			className={css}
			shortcuts={keyCommands.goToDefaultHome}
			onClick={this.props.onGoHome} />
	}
}

export class Logo extends React.Component<{
	productName: string
}, {}> {
	render() {
		let css = "s_profile_bar_logo";
		if (this.props.productName) {
			css += " s_profile_bar_logo_" + this.props.productName.trim().toLowerCase();
		}
		return <div className={css} />
	}
}