"use strict";
const jsurl = require("jsurl");

export interface IJsUrlProfile {
	loc?: string,
	role?: string,
	ep?: string
}

export function stringify(loc: string, role?: string, ep?: string): string {
	let value: IJsUrlProfile = {
		loc: loc
	};
	if (role) {
		value.role = role;
	}
	if (ep) {
		value.ep = ep;
	}
	return jsurl.stringify(value);
}
export function parse(value: string): IJsUrlProfile {
	if (value && value != "{$userprofile}") {
		return jsurl.parse(value);
	}
}