"use strict";

export function logHidden(node: HTMLElement, hidden: boolean) {
    if (hidden) {
        node.setAttribute("data-s-hidden", "1");
    }
    else {
        node.removeAttribute("data-s-hidden");
    }
}

export function logPickers(item: any) {
    if (item.buttons) {
        for (let btn of item.buttons) {
            btn.pickerId && btn.link.setAttribute("data-s-picker", btn.pickerId);
        }
    }
}

export function logSection(section: any) {
    if (section.domItem) {
        if (section.isTabSection && section.header && section.header.parentNode) {
            section.header.parentNode.setAttribute("data-s-tabs", "1");
        }
        section.domItem.setAttribute("data-s-open", section.$opened ? "1" : "0");
        if (section.isMenuGroup) {
            section.domItem.setAttribute("data-s-menu-group", section.getTitle());
        }
    }
}

export function logMenu(menuItem: any) {
    menuItem && menuItem.getTitle && menuItem.domItem.setAttribute("data-s-menu-title", menuItem.getTitle());
}
