"use strict";

const key = "?url=";
const keyLength = key.length;

export function encode(url: string) {
    if (url.indexOf(key) < 0) {
        return key + encodeURIComponent(url);
    }
    return url;
}

export function decode(url: string) {
    let index = url.indexOf(key);
    if (index >= 0) {
        return decodeURIComponent(url).slice(index + keyLength);
    }
    return url;
}