"use strict";
import * as React from 'react';

interface ISectionBodyProps {
    className: string,
    noHeader: boolean,
    useFlowLayout: boolean,
    collapsed: boolean
}

export class SectionBody extends React.Component<ISectionBodyProps, {}> {
    render() {
        let content: any;
        if (!this.props.collapsed && this.props.children) {
            if (this.props.useFlowLayout) {
                content = <div className="s_flow_layout_fields">{this.props.children}</div>;
            }
            else {
                content = <div className="row">
                    <div className="col-lg-12">{this.props.children}</div>
                </div>;
            }
        }
        let css = this.props.className + "_body";
        if (this.props.collapsed) {
            css += " s_section_collapsed";
        }
        if (this.props.noHeader) {
            css += " s_section_no_header";
        }
        return <div className={css}>{content}</div>;
    }
}