"use strict";
import * as React from 'react';
import * as ReactDom from 'react-dom';
import { IPageLoadError } from '../../page/pageLoader';
import { AlertPage } from '../../alert/page/alertPage';

export class SyraAlertPage {
    constructor(public root: HTMLElement,private props: IPageLoadError) {
        ReactDom.render(<AlertPage {...this.props} />, this.root);
     }
    dispose() {
        if (this.root) {
            ReactDom.unmountComponentAtNode(this.root);
            this.props = this.root = null;
        }
    }
}