"use strict";
import * as React from 'react';
import * as ReactDom from 'react-dom';
import { Breadcrumb } from '../navigation/breadcrumb/breadcrumb';
import { currentProfile } from '../profile/profile';
import { SitemapModule } from '../navigation/sitemap/groups';


export class SyraBreadcrumb {
    slot: HTMLElement;
    constructor(syraPage: any) {
        this.slot = syraPage.header;
        let disabled = syraPage.inModal || syraPage.$facet === "$modal2";
        ReactDom.render(<Breadcrumb
            url={syraPage.urlSeg}
            steps={syraPage.$breadCrumb}
            disabled={disabled}
            inModal={syraPage.inModal}
            modules={currentProfile.sitemap && currentProfile.sitemap.modules} />, this.slot);
    }
    dispose() {
        this.slot && ReactDom.unmountComponentAtNode(this.slot);
        this.slot = null;
    }
}
