"use strict";
interface ICoreDataType {
    date: any,
    datetime: any,
    time: any,
    integer: any,
    decimal: any,
    real: any
}


class Core {
    get dataTypes(): ICoreDataType {
        return ((window as any).syra_core).dataTypes as ICoreDataType;
    }
    escape(data: string): string {
        return ((window as any).syra_core).escape(data);
    }
}

export const core = new Core();