"use strict";
import * as React from 'react';
import * as Sdata from '../../dataStore/sdata';
import { Diagnose } from '../../alert/diagnose/diagnose';

export class TrackerDiagnoses extends React.Component<{
    $diagnoses: Sdata.IDiagnose[]
}, {}>{
    render() {
        if (this.props.$diagnoses && this.props.$diagnoses.length) {
            let content: any[] = [];
            for (let diagnose of this.props.$diagnoses) {
                content.unshift(<Diagnose key={"diag_" + content.length}
                    severity={diagnose.$severity}
                    stackTrace={diagnose.$stackTrace}
                    message={diagnose.$message} />);
            }
            return <li className="s_tracker_diagnoses_slot">
                <ul className="s_tracker_diagnoses">
                    {content}
                </ul>
            </li>;
        }
        return <noscript />;
    }
}