"use strict";
class Browser {
    constructor(public name: string, public version: string) {

    }
    get landscape(): boolean {
        return window.orientation && (window.orientation == 90 || window.orientation == -90);
    }
    get isFireFox(): boolean {
        return this.name == "firefox";
    }
    get isSafari(): boolean {
        return this.name == "safari";
    }
    get isMSIE(): boolean {
        return this.name == "msie";
    }
}

export let browser: Browser;

export function initialize(name: string, info: any) {
    browser = new Browser(name, info);
}