"use strict";

const LEFT = "left";
const RIGHT = "right";

export default class RTL {
    private _on: boolean;
    constructor() {
        this._on = document.dir === 'rtl';
    }
    get on(): boolean {
        return this._on;
    }
    apply(xDirection: string): string {
        if (this.on) {
            if (xDirection.indexOf(LEFT) >= 0) {
                return xDirection.replace(LEFT, RIGHT);
            }
            return xDirection.replace(RIGHT, LEFT);
        }
        return xDirection;
    }
}