"use strict";

const BODY_ID= "s_app_body";
const SLOT_ID= "s_app_slot";

class Node{
    private _node: HTMLElement;
    constructor(private _id:string){

    }
     get id():string{
        return this._id;
    }
    get node(): HTMLElement{
        if(!this._node){
            this._node = document.getElementById(this._id);
        }
        return this._node;
    }
    get width(): number{
        return this.node.clientWidth;
    }

    get height(): number{
        return this.node.clientHeight
    }   
}

export default class SiteDom{
    private _body: Node;
    private _slot: HTMLElement;
    
    get slot(): HTMLElement{
        if(!this._slot){
            this._slot = document.getElementById(SLOT_ID);
        }
        return this._slot;
    }   
    get body(): Node{
        if(!this._body){
            this._body = new Node(BODY_ID);
        }
        return this._body;
    }   
}