"use strict";

class Storage {
    constructor(private storage: any) {
        if (!this.storage) {
            this.storage = {};
        }
    }
    get(key: string) {
        return this.storage[key];
    }
    set(key: string, value: string) {
        if (value !== null) {
            this.storage[key] = value;
        }
        else {
            this.remove(key);
        }
    }
    getJson(key: string) {
        let objStr = this.storage[key];
        return objStr && JSON.parse(objStr);
    }
    setJson(key: string, obj: any) {
        if (obj !== null) {
            this.storage[key] = JSON.stringify(obj);
        }
        else {
            this.remove(key);
        }
    }
    private remove(key: string) {
        if (this.storage.removeItem) {
            this.storage.removeItem(key);
        }
        else {
            delete this.storage[key];
        }
    }
}

export const local = new Storage(localStorage);
export const session = new Storage(sessionStorage);