"use strict";
var path = require('path');
const webpackMerge = require('webpack-merge');
var webpack = require('webpack');
const commonConfig = require('./base');
var ExtractTextPlugin = require("extract-text-webpack-plugin");

module.exports = function(env) {
	return webpackMerge(commonConfig, {
		/*
		Combination of path and filename tells webpack where and what 
		to name bundled output file
		*/

		output: {
			// path: path.resolve(__dirname, options && options.output ? options.output : "../../../Syracuse/node_modules/syracuse-main/html"),
			// publicPath:  "../",
			filename: "js/app_debug.js"
		},

		// Enable sourcemaps for debugging webpack's output.
		devtool: "source-map",
		plugins: [
			new ExtractTextPlugin('./styles/app_debug.css')
		]
	});
};