"use strict";
var helpers = require('@sage/syracuse-core').helpers;

function JSONViewer() {}

exports.JSONViewer = helpers.defineClass(JSONViewer, null, {
	render: function(parent, value) {
		try {
			syra_dom.pre("s-pre", JSON.stringify(JSON.parse(value), null, 2), parent);
		} catch (error) {
			return syra_local.field_richdata_json_error;
		}
	},
	dispose: function() {

	}
});