"use strict";
var officeSiteAdapter = require('syracuse-ui/lib/office/officeSiteAdapter');
var PptPage = require("syracuse-ui/lib/office/ppt/pptPage").PptPage;
var PptInterface = require('syracuse-ui/lib/office/ppt/pptInterface').PptInterface;

exports.checkLogon = function() {
		syra_site.isOfficeSite = true;
		officeSiteAdapter.initialize(syra_site);
		syra_pageBuilder.types.ppt_populate_worksheet = PptPage;
		syra_site.pptInterface = new PptInterface();
		syra_site.pptInterface.onLogin();
		syra_site.pptInterface.getAddinVersion();
	}
