"use strict";
var officeSiteAdapter = require('@sage/syracuse-ui/lib/office/officeSiteAdapter');
var WordPage = require("@sage/syracuse-ui/lib/office/word/wordPage").WordPage;
var WordReportPage = require("@sage/syracuse-ui/lib/office/word/wordReportPage").WordReportPage;
var WordInterface = require('@sage/syracuse-ui/lib/office/word/wordInterface').WordInterface;


exports.checkLogon = function() {
		syra_site.isOfficeSite = true;
		officeSiteAdapter.initialize(syra_site);
		syra_pageBuilder.types.wordmailmerge = WordPage;
		syra_pageBuilder.types.wordreport = WordReportPage;
		syra_site.wordInterface = new WordInterface();
		syra_site.wordInterface.onLogin();
	}
