"use strict";
function Process(){
    this.jsonTxt = "";
    this.json = "";
    this.jsonFile = "";
    this.getCurrentLayerId = function(){
        (this.json != "") ? this.json.currentLayerId : null;
    };
    this.getActions = function(){
        if (this.json != "") {
            return this.json.acts.a;
        }
        else {
            return [];
        }
    };
    this.getFormes = function(){
        (this.json != "") ? this.json.layersArr : [];
    };
    this.getJson = function(){
        (this.jsonTxt != "") ? this.jsonTxt : null;
    };
    this.loadJson = function(json){
        if (json != "") {
            this.jsonTxt = json;
            try {
                this.json = $.parseJSON(json);
            } 
            catch (e) {
                // si le parse ne fonctionne pas, on renvoie l'objet et on garde les éléments vide.
                this.jsonTxt = "";
                this.json = "";
                return this;
            }
        }
        return this;
    };
};
