require('streamline').register();
var sagepay = require('bundles/sage-pay');
var tokenRegistrationTest = require('bundles/sage-pay/tokenRegistrationTest');

var http = require('http');
var dispatcher = require('httpdispatcher');

//Lets define a port we want to listen to
const PORT=8080; 

//Create a server
var server = http.createServer(handleRequest);

// Start the server
server.listen(PORT, function(){
    console.log("Server listening on port: %s", PORT);
});

function handleRequest(request, response){
    try {
        //log the request on console
        console.log(request.url);
        //Disptach
        dispatcher.dispatch(request, response);
    } catch(err) {
        console.log(err);
    }
}

dispatcher.setStatic('resources');

dispatcher.onPost("/createToken", function(req, res) {
    var obj = tokenRegistrationTest.createTokenRequest(JSON.parse(req.body));
    res.writeHead(200, {'Content-Type': 'application/json'});
    res.end(obj);
}); 

dispatcher.onGet("/getRedirectUrl", function(req, res) {
    res.writeHead(200, {'Content-Type': 'text/plain'});
    res.end('getRedirect');

    tokenRegistrationTest.getRedirectUrl();
});   

dispatcher.onPost("/bundles/sage-pay/redirect", function(req, res) {
    res.writeHead(200, {'Content-Type': 'text/plain'});
    res.end('/bundles/sage-pay/redirect');

    tokenRegistrationTest.sendSPSNotificationResponse(req, res);
});