"use strict" ;

var bcd = require('./bcd') ;
var integer = require('./integer') ;
var util = require('./util');

var cnvRules = [] ;

exports.toDouble = function(v) {
	if (typeof v === "number") 
		return v ;
	else {
		if (bcd.isBCD(v)) return bcd.toDouble( v ) ;
		else if (integer.isInteger(v)) return integer.toDouble( v ) ;
		else throw util.badOperand(v) ;
	}
}

