"use strict";
var util = require('./util');
var errors = require('./errors');
var X3Error = errors.X3Error;
var glob = require('streamline/lib/globals');
var bcd = require('etna-engine/lib/runtime/bcd') ;
var operators = require('./operators').instructions;

function fac(n) {
	return (n > 1) ? n * fac(n - 1) : 1;
}
function anp(n, p) {
	return fac(n)/fac(n-p);
}

function toRadians(angle) {
	var frame = glob.context.x3frame;
	switch(frame.context.sys.values.adxmda) {
		case 0 : return angle * (Math.PI / 180); //Degrees => Radians
		case 2 : return angle * (Math.PI / 200); //Grades => Radians
	}
	return angle;
}

exports.functions = {
	ABS: {
		count: 1,
		fn: function(x) {
			util.checkNumber(x);
			return Math.abs(x);
		}
	},
	MOD: {
		count: 2,
		fn: function mod(x, y) {
			util.checkNumber(x);
			util.checkNumber(y);
			util.checkNotZero(y);
			return x >= 0 ? x % y : -(-x % y);
		}
	},
	RND: {
		count: 1,
		fn: function(x) {
			return x * Math.random();
		}
	},
	SGN: {
		count: 1,
		fn: function(x) {
			return x == 0 ? 0 : x > 0 ? 1 : -1;
		}
	},
	SQR: {
		count: 1,
		fn: Math.sqrt
	},
	AR2: {
		count: 1,
		fn: function(v) {
			return Math.floor(v * 100 + 0.5) / 100;
		}
	},
	ARR: {
		count: 2,
		fn: function(x, y) {
			if ((typeof y === "number") && (y === 0)) return( x ) ;
			if (bcd.isBCD(y) && bcd.isZero(y)) return ( x ) ;

			var val = operators.ADD( bcd.fromDouble(0.5), operators.DIV( x, y) ) ;
			return bcd.toInt32( operators.MUL( y, val )) ;
		}
	},
	FIX: {
		count: 1,
		fn: function(v) {
			return v >= 0 ? Math.floor(v) : -Math.floor(-v);
		}
	},
	INT: {
		count: 1,
		fn: Math.floor

	},
	ANP: {
		count: 2,
		fn: anp
	},
	CNP: {
		count: 2,
		fn: function(n, p) {
			return anp(n, p) / anp(p);
		}
	},
	FAC: {
		count: 1,
		fn: fac
	},
	COS: {
		count: 1,
		fn: function(angle) {
			return Math.cos(toRadians(angle));
		}
	},
	SIN: {
		count: 1,
		fn: function(angle) {
			return Math.sin(toRadians(angle));
		}
	},
	TAN: {
		count: 1,
		fn: function(angle) {
			return Math.tan(toRadians(angle));
		}
	},
	ACOS: {
		count: 1,
		fn: Math.acos
	},
	ASIN: {
		count: 1,
		fn: Math.asin
	},
	ATAN: {
		count: 1,
		fn: Math.atan
	},
	ACH: {
		count: 1,
		fn: function(x) {
			return Math.log(x + Math.sqrt(x * x - 1));
		}
	},
	ASH: {
		count: 1,
		fn: function(x) {
			return Math.log(x + Math.sqrt(x * x + 1));
		}
	},
	ATH: {
		count: 1,
		fn: function(x) {
			return Math.log((1 + x) / (1 - x)) / 2;
		}
	},
	CH: {
		count: 1,
		fn: function(x) {
			return (Math.exp(x) + Math.exp(-x)) / 2;
		}
	},
	SH: {
		count: 1,
		fn: function(x) {
			return (Math.exp(x) - Math.exp(-x)) / 2;
		}
	},
	TH: {
		count: 1,
		fn: function(x) {
			return (Math.exp(2 * x) - 1) / (Math.exp(2 * x) + 1);
		}
	},
	EXP: {
		count: 1,
		fn: Math.exp
	},
	LN: {
		count: 1,
		fn: Math.log
	},
	LOG: {
		count: 1,
		fn: function(val) {
			return Math.log(val) / Math.LN10;
		}
	},
	PI: {
		count: 0,
		fn: function() {
			return Math.PI;
		}
	},
	SETBIT: {
		count: 3,
		fn: function(val,pos,bit) {
			val |= (bit << (pos-1));
			return val;
		}
	},
	GETBIT: {
		count: 2,
		fn: function(val,pos) {
			return (val & (1 << (pos-1)))?1:0;
		}
	}
};