'use strict';

function block(list) {
	return {
		$items: list.map(name => ({
			$bind: name
		}))
	};
}

exports.representation = {
	$entityName: 'x3jsUnitTestResult',
	$facets: {
		$details: {
			$layout: {
				$items: [{
					$bind: 'stamp',
				}, {
					$bind: 'passed',
				}, {
					$bind: 'duration',
				}, {
					$bind: 'summary',
				}, {
					$bind: 'suiteResults',
					$layout: block(['unitTest', 'passed', 'duration', 'caseResults']),
				}],
			},
		},
	},
};