'use strict';

function block(list) {
	return {
		$items: list.map(name => ({
			$bind: name
		}))
	};
}

exports.representation = {
	$entityName: 'x3jsUnitTestResult',
	$facets: {
		$details: {
			$layout: {
				$items: [{
					$bind: 'unitTest',
				}, {
					$bind: 'stamp',
				}, {
					$bind: 'passed',
				}, {
					$bind: 'duration',
				}, {
					$bind: 'summary',
				}, {
					$bind: 'caseResults',
				}],
			},
		},
		$query: {
			$layout: {
				$items: [{
					$bind: "$resources",
					$layout: block(['unitTest', 'stamp', 'passed', 'duration', 'summary', 'caseResults']),
				}],
			},
		},
	},
};