"use strict" ;


exports.uuidType = 1 ;
exports.blobType = 2 ;

function _SQLBUFFER( type, buf ) {
	
	switch ( type )
	{
		case exports.uuidType : //this.uuidType :
			this.type = exports.uuidType ;
			this.value = buf ;
			return this ;
		case exports.blobType :
			this.type = exports.blobType ;
			this.value = buf ;
			return this ;
		default :
			return undefined;
	}
}

exports.SQLBUFFER = _SQLBUFFER ;
