"use strict";

const X3Error = require("./runtime/errors").X3Error;

export function print(str) {
	console.error(str);
};

export function fail(message) {
	throw new Error(message);
};
export function printMemory() {
	console.error(process.memoryUsage());
};
export function printStack() {
	console.error(new X3Error(0, "stack dump").toString());
};