const BCD_ZERO = exports.ZERO = fromString('0');
const BCD_ONE_HALF = exports.ONE_HALF = fromString('0.5');
const BCD_ONE = exports.ONE = fromString('1');
const BCD_MAX = exports.MAX = fromDouble(MAXDBL * (1 - 1 / FLOATPREC));
const BCD_MIN = exports.MIN = fromDouble(-MAXDBL * (1 - 1 / FLOATPREC));
const BCD_MAX_INT32 = exports.MAX_INT32 = fromInt32(MAXESG4);
const BCD_MIN_INT32 = exports.MIN_INT32 = fromInt32(MINESG4);

