"use strict";

import * as types from './types';
import * as util from './util';
const tracer = require('syracuse-core').getTracer("etna.engine");

export class DBTableParent {
	local: string;
	superv: any; // see later
	driver: types.Driver;
	abbrev: string;
	slot: number;
	types: types.VariableType[];
	values: any[];

	inFrontOf: DBTableParent;
	constructor(driver: types.Driver, abbrev: string, superv: any, loc: string) {
		this.local = loc;
		this.superv = superv;
		this.driver = driver;
		this.abbrev = abbrev;
		this.slot = 0;
		this.types = util.any({});
		this.values = util.any({});
		this.inFrontOf = undefined;
	}

	init(_) {
		return this;
	}
}

;