"use strict";

import * as util from './util';
const tracer = require('syracuse-core').getTracer("etna.engine");

class DBTableParent {
	constructor(driver, abbrev, superv, loc) {
		this.local = loc;
		this.superv = superv;
		this.driver = driver;
		this.abbrev = abbrev;
		this.slot = 0;
		this.types = {};
		this.values = {};
		this.inFrontOf = undefined;
	}

	init(_) {
		return this;
	}
}

exports.DBTableParent = DBTableParent;