import { _ } from 'streamline-runtime';
import * as types from './types';
import * as util from './util';
import { DBTable } from './dbtable';
const tracer = require('syracuse-core').getTracer("etna.engine");

export class DBTableParent {
	local: number;
	superv: any; // see later
	driver: types.Driver;
	abbrev: string;
	slot: number;
	types: types.VariableType[];
	values: any[];
	inFrontOf: DBTable;
	constructor(driver: types.Driver, abbrev: string, superv: any, loc: number) {
		this.local = loc;
		this.superv = superv;
		this.driver = driver;
		this.abbrev = abbrev;
		this.slot = 0;
		this.types = util.any({});
		this.values = util.any({});
		this.inFrontOf = undefined;
	}

	init(_: _) {
		return this;
	}
}
