import { _ } from "streamline-runtime";
export interface Frame {}
export interface Dict {}

export type VariableOp = (frame: Frame, dict: Dict, field: string, val: any, silent: boolean, strict: boolean) => any;

export interface ParseNode {
	tag: string;
}

export interface Expression<T> {
	(_: _, op?: VariableOp): T;
	value?: T;
}
