import { _ } from 'streamline-runtime';
import * as http from '../http';
import * as util from '../../supervisor/util';

export class ASYR {
	httpContext: http.HttpContext;
	$exported: boolean;

	constructor(httpContext: http.HttpContext) {
		this.httpContext = httpContext;
		this.$exported = true;
	}
	get(_: _,  name: string) {
		switch (name) {
			case "PREFIX":
				return "/sdata/x3/erp/" + this.httpContext.supervisor.endpointName;
		}
		return "";
	}
};
