import { _ } from 'streamline-runtime';
import * as util from '../../supervisor/util';

class ASYR {
	constructor(httpContext) {
		this.httpContext = httpContext;
		this.$exported = true;
	}
	get(_: _,  name) {
		switch (name) {
			case "PREFIX":
				return "/sdata/x3/erp/" + this.httpContext.supervisor.endpointName;
		}
		return "";
	}
};

exports.Constructor = ASYR;