"use strict";

import * as snapshots from '../meta/snapshots';
import { BaseInstance } from './types';

const tracerJs = require('syracuse-core').getTracer("etna.supervisor");

export class Snapshot {
	instance: BaseInstance;
	snapshotCreated: boolean;

	constructor(instance: BaseInstance) {
		this.instance = instance;
		this.snapshotCreated = false;
	}
	enable() {
		if (this.snapshotCreated === false) {
			this.instance.enableSnapshot();
		}
		this.snapshotCreated = true;
	}
	disable() {
		this.instance.disableSnapshot();
		//this.disableSnapshot();
		this.snapshotCreated = false;
	}
};

snapshots.addSnapshots(Snapshot.prototype);

;