import { _ } from 'streamline-runtime';
import * as util from '../util';
import { ExportedPropertyMeta } from '../metadata/representation';
import { Representation } from './types';
import { Supervisor } from '../supervisor';

export class ExportedProperty {
	supervisor: Supervisor;
	representation: Representation;
	data: ExportedPropertyMeta;
	tag: string;
	constructor(superv: Supervisor, repr: Representation, data: ExportedPropertyMeta) {
		this.supervisor = superv;
		this.data = data;
		this.representation = repr;
	}
	init(_: _) {
		return this;
	}
};

export const Constructor = ExportedProperty;